import axios from "axios";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
var Buffer = require('buffer/').Buffer

export default function Header() {
    let history = useNavigate();
    const [disableButton, setDisableButton] = useState(false);
    const [profileImage, setProfileImage] = useState("data:image/png;base64,")

    useEffect(() => {
        fetchProfileImage()
    }, [])
    
    const logout = async () => {
        setDisableButton(true);
        await axios.post('https://api-nodejs-todolist.herokuapp.com/user/logout', {}, {
            headers : {
                Authorization: localStorage.getItem('token'),
            },
        }).then((res) => {
            localStorage.removeItem('token')
            history('/')
        }).catch((err) => {
            console.log(err)
        })
        setDisableButton(false);
    }


    const fetchProfileImage = () => {
        axios.get(`https://api-nodejs-todolist.herokuapp.com/user/${localStorage.getItem('userId')}/avatar`, {
            headers : {
                Authorization : localStorage.getItem('token')
            },
            responseType: 'arraybuffer'
        }).then((res) => {
            let base64ImageString = Buffer.from(res.data, 'binary').toString('base64')
            setProfileImage(`data:image/png;base64,${base64ImageString}`)
        }).catch((err) => {
            console.log(err)
        })
    }

     
    return  (
        <section className = "mt-5 container mb-3">
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <h1 className="navbar-brand font-weight-bold">
                    <img 
                        width="40"
                        height="40"
                        className = "img-fluid rounded-circle mr-3" 
                        src= {`${profileImage}`}
                    > 
                    </img>
                    My To-Do List Application
                </h1>
                <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav">
                        <li className="nav-item active">
                            <a className="nav-link" href="/home">Home <span className="sr-only">(current)</span></a>
                        </li>
                        <li className="nav-item mr-2">
                            <a href = "/profile" onClick={() => {}} className = "float-right nav-link">Profile</a>
                        </li>
                        <li className="nav-item float-right">
                            <button 
                                onClick={() => {logout()}} 
                                className = "btn btn-dark float-right"
                                disabled = {disableButton}
                            >
                                Log out
                            </button>
                        </li>
                    </ul>
                </div>
            </nav>
        </section>
    );
}